<style lang="scss">
@use "sass:math";
@use "~/assets/scss/carbon.scss";
@use "~/assets/scss/helpers/index.scss" as qiskit;

.dark-header {
  @include qiskit.responsive-grid-bg-strip(
    "/images/grid/grid-hero-dark.svg",
    auto,
    56rem
  );

  position: relative;
  overflow: hidden;

  // In Figma, the height is not enforced but the background is always
  // visible completely so we do it in the CSS. A small correction is needed
  // to be able of displaying the bottom lines of the grid.
  height: calc(56rem + 2px);

  @include carbon.breakpoint-between(md, lg) {
    // To adjust to the size of the smaller grid.
    height: calc(#{math.div(56rem * 40, 64)} + 2px);
  }

  @include carbon.breakpoint-down(md) {
    background-position: top -5rem center;
    height: calc(#{math.div(48rem * 40, 64)} + 2px);
  }

  @include carbon.breakpoint-down(sm) {
    background-position: top -12rem center;
    height: calc(#{math.div(36rem * 40, 64)} + 2px);
  }

  &__container {
    @include qiskit.contained;

    position: relative;
    display: flex;
    align-items: center;
    padding-top: 25rem;

    @include carbon.breakpoint-between(md, lg) {
      padding-top: 18.75rem;
    }

    @include carbon.breakpoint-down(md) {
      padding-top: 12.5rem;
    }

    @include carbon.breakpoint-down(sm) {
      padding-top: 11rem;
    }
  }

  &__title {
    position: relative;
    z-index: 1;

    @include carbon.breakpoint-between(md, lg) {
      max-width: 28rem;
    }

    @include carbon.breakpoint-down(md) {
      max-width: 14rem;
    }

    @include carbon.breakpoint-down(sm) {
      max-width: 12.5rem;
    }
  }

  &__media {
    position: absolute;
    top: 0;
    right: auto;
    left: 23rem;
    bottom: 0;
    width: 44rem;

    @include carbon.breakpoint-up(xlg) {
      right: -8rem;
    }

    @include carbon.breakpoint-down(lg) {
      width: 32rem;
      right: auto;
      left: 15rem;
    }

    @include carbon.breakpoint-down(md) {
      width: 22rem;
      right: auto;
      left: 6.5rem;
    }

    @include carbon.breakpoint-down(sm) {
      width: 18rem;
      right: auto;
      left: 6rem;
    }
  }

  &__media-cryo {
    width: 100%;
  }

  &__media-transmon {
    position: absolute;
    top: 24.5rem;
    right: 9.25rem;
    width: 19rem;
    z-index: 1;

    @include carbon.breakpoint-between(md, lg) {
      top: 19.5rem;
      right: 7.5rem;
      width: 12rem;
    }

    @include carbon.breakpoint-down(md) {
      top: 14rem;
      right: 5.5rem;
      width: 8rem;
    }

    @include carbon.breakpoint-down(sm) {
      top: 12.75rem;
      right: auto;
      width: 6rem;
      left: 7.5rem;
    }
  }

  &__media-transmon-outline {
    position: absolute;
    top: 24.5rem;
    right: 9rem;
    width: initial;
    z-index: 1;

    @include carbon.breakpoint-between(md, lg) {
      top: 20rem;
      right: 8rem;
      z-index: 1;
      max-width: 16rem;
    }

    @include carbon.breakpoint-down(md) {
      top: 13.85rem;
      max-width: 12rem;
      right: 5.25rem;
    }

    @include carbon.breakpoint-down(sm) {
      top: 13rem;
      max-width: 8rem;
      right: auto;
      left: 5.15rem;
    }
  }
}
</style>
